import { AbstractApi } from './AbstractApi';
export class ClientApi extends AbstractApi {
    async getClientList() {
        return this._restClient.makeRequest({
            method: 'GET',
            path: '/clients',
        });
    }
    async getClient(clientId) {
        this.requireId(clientId);
        return this._restClient.makeRequest({
            method: 'GET',
            path: `/clients/${clientId}`,
        });
    }
    verifyClient(token) {
        return this._restClient.makeRequest({
            method: 'POST',
            path: '/clients/verify',
            bodyParams: { token },
        });
    }
}
